part1 :: IO ()
part1 = do
  intcode <- initIntcode <$> inputFile "src/Task06/input.txt"

  (screen,_) <- execIntegrationT runArcade defaultArcade intcode
  print $ M.size $ M.filter (== Block) $ _screen screen

part2 :: IO ()
part2 = do
  playGame <- prompt "Do you want to see the game? (y/<anything else>): "

