    finalScore = 15988

data Tile = Empty
          | Wall
          | Block
          | Paddle
          | Ball
  deriving (Show, Eq, Enum)

data Screen = Screen {_score :: Int, _screen :: Map (Int,Int) Tile}

data InputBuffer = None
                 | One Int
                 | Two Int Int
                 | Complete Int Int Int

type Arcade = (Screen, InputBuffer)
defaultArcade = (Screen 0 M.empty, None)

screen :: Arcade -> Screen
screen (x,_) = x
inputBuffer :: Arcade -> InputBuffer
inputBuffer (_,x) = x

addScreenInput :: Int -> IntegrationIO Arcade ()
addScreenInput input = do
  buffer <- gets inputBuffer
  let buffer' = case buffer of
        None    -> One input
        One x   -> Two x input
        Two x y -> Complete x y input
        _ -> error "Already Completed Input"

  modify $ \(screen,_) -> (screen,buffer')

addTile :: Screen -> Int -> Int -> Int -> Screen
addTile screen (-1) 0 score = screen {_score = score}
addTile screen x y tileId   = screen {_screen = M.insert (x,y) (toEnum tileId) $ _screen screen}

runArcade :: IntegrationIO Arcade ()
runArcade = void $ runIntegration handleInput handleOutput
  where
    handleInput :: IntegrationIO Arcade ()
    handleInput = do
      liftIO . _drawScreen =<< gets screen

      input <- liftIO getChar
      liftIO $ putStr "\b"
      case input of
        'a' -> lift $ pushInput (-1)
        's' -> lift $ pushInput 0
        'd' -> lift $ pushInput 1
        _  -> pure ()

    handleOutput :: IntegrationIO Arcade ()
    handleOutput = do
      screenInput <- lift $ popOutput
      addScreenInput screenInput

      (screen,buffer) <- get
      case buffer of
        Complete x y tileId -> modify $ \(screen,_) -> (addTile screen x y tileId, None)
        _                   -> pure ()

runArcadeWithInputs :: IntegrationIO (Arcade, [Int]) ()
runArcadeWithInputs = void $ runIntegration handleInput handleOutput
  where
    handleInput :: IntegrationIO (Arcade, [Int]) ()
    handleInput = do
      liftIO $ threadDelay 50000
      liftIO $ putStr $ (toEnum 27) : "[H" ++ [toEnum 27] ++ "[J"
      liftIO . _drawScreen =<< gets (screen . fst)

      (input:queue) <- gets snd
      lift $ pushInput input
      modify $ \(arcade,_) -> (arcade, queue)

    handleOutput :: IntegrationIO (Arcade, [Int]) ()
    handleOutput = do
      screenInput <- lift $ popOutput
      addScreenWithInputs screenInput

      (screen,buffer) <- gets fst
      case buffer of
        Complete x y tileId -> modify $ \((screen,_),queue) -> ((addTile screen x y tileId, None),queue)
        _                   -> pure ()

addScreenWithInputs :: Int -> IntegrationIO (Arcade, [Int]) ()
addScreenWithInputs input = do
  buffer <- gets (inputBuffer . fst)
  let buffer' = case buffer of
        None    -> One input
        One x   -> Two x input
        Two x y -> Complete x y input
        _ -> error "Already Completed Input"

  modify $ \((screen,_),queue) -> ((screen,buffer'), queue)

_drawScreen :: Screen -> IO ()
_drawScreen Screen{_score = score, _screen = screen} = do
  putStrLn $ "Score: " ++ show score
  mapM_ (putStrLn . concatMap _drawTile) $ screenToList screen
  where
    screenToList :: Map (Int,Int) Tile -> [[Tile]]
    screenToList = map (map snd) . groupBy (\((_,y1),_) ((_,y2),_) -> y1 == y2) . sortOn (snd . fst) . M.assocs

_drawTile :: Tile -> String
_drawTile Empty  = " "
_drawTile Wall   = "\x1b[40m \x1b[49m"
_drawTile Block  = "\x1b[44m \x1b[49m"
_drawTile Paddle = "▬"
_drawTile Ball   = "●"




